  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "SEO Research Output",
  "type": "object",
  "properties": {
    "search_intent": {
      "type": "string",
      "enum": ["informational", "transactional", "navigational", "commercial"]
    },
    "writing_style": {
      "type": "string",
      "description": "Describes the overall writing style, e.g., engaging and storytelling, data-driven and technical, concise and professional."
    },
    "writing_tone": {
      "type": "string",
      "description": "Defines the tone of the writing, e.g., friendly and conversational, persuasive and compelling, formal and authoritative."
    },
    "hidden_insight": {
      "type": "string",
      "description": "Unique or uncommon insight derived from SERP analysis, or 'No significant insights detected beyond existing content trends.'"
    },
    "target_audience": {
      "type": "string",
      "description": "Specifies who the content is meant for, such as marketers, tech enthusiasts, small business owners, etc."
    },
    "goal_of_article": {
      "type": "string",
      "description": "The main objective or purpose of the article based on search intent and SERP analysis."
    },
    "semantic_analysis": {
      "type": "object",
      "properties": {
        "common_subtopics": {
          "type": "array",
          "items": { "type": "string" },
          "minItems": 1,
          "description": "List of common subtopics found across top SERP results."
        },
        "related_questions": {
          "type": "array",
          "items": { "type": "string" },
          "minItems": 1,
          "description": "Common related questions found via SERP or People Also Ask."
        }
      },
      "required": ["common_subtopics", "related_questions"]
    },
    "keywords": {
      "type": "object",
      "properties": {
        "primary_keyword": {
          "type": "string",
          "description": "The main keyword focus of the article."
        },
        "secondary_keywords": {
          "type": "array",
          "items": { "type": "string" },
          "minItems": 1
        },
        "semantic_keywords": {
          "type": "array",
          "items": { "type": "string" },
          "minItems": 1
        },
        "long_tail_keywords": {
          "type": "array",
          "items": { "type": "string" },
          "minItems": 1
        }
      },
      "required": [
        "primary_keyword",
        "secondary_keywords",
        "semantic_keywords",
        "long_tail_keywords"
      ]
    }
  },
  "required": [
    "search_intent",
    "writing_style",
    "writing_tone",
    "hidden_insight",
    "target_audience",
    "goal_of_article",
    "semantic_analysis",
    "keywords"
  ],
  "additionalProperties": false
}

