parameters.prompt:
=Generate an AI image prompt that visually represents the following article. Ensure the description is detailed, engaging, and optimized for an eye-catching featured image.

Title: {{ $json.title }}
Description: {{ $json.description }}

parameters.jsCode:
const title = $('Set Post New Title').first().json.new_title
const seo_title = $('Set Post Metadata').first().json.title
const seo_description = $('Set Post Metadata').first().json.description
const image = $('Set Post Image').first().json.url
const final_post = $('Set Final Post').first().json.final_post

const data = `
## Title: ${title}
\n
*SEO Title:* ${seo_title}
\n
*SEO Description:* ${seo_description}
\n
*Featured Image Link:*
${image}
\n
*Post Content:*
${final_post}
`;


return {
  json : {
    data
  }
}
