// --- Configuration ---
// The desired horizontal space between the leftmost node and the sticky note
const X_OFFSET = 32;

// --- Main Logic ---
const inpdata = $input.first();
let finalWorkflow = {};

// 1. Decode the workflow from Base64
// We only proceed if the 'content' field exists.
if (inpdata?.json?.content) {
  try {
    const buffer = Buffer.from(inpdata.json.content, 'base64');
    finalWorkflow = JSON.parse(buffer.toString('utf8'));
  } catch (error) {
    throw new Error(`Failed to parse workflow JSON from Base64 content. Error: ${error.message}`);
  }
} else {
  throw new Error("Input data is missing the 'content' field from GitHub.");
}

// 2. Get the sticky note object from the input
const stickyNote = inpdata.json.sticky_note;
if (!stickyNote) {
  throw new Error("Input data is missing the 'sticky_note' object.");
}

// 3. Position the sticky note
// Check if the decoded workflow has any nodes to align with.
if (!finalWorkflow.nodes || finalWorkflow.nodes.length === 0) {
  // If there are no nodes, we can't align to anything.
  // The sticky note will keep its default position.
} else {
  // Find the leftmost node in the workflow
  const leftmostNode = finalWorkflow.nodes.reduce((leftmost, currentNode) => {
    const leftmostX = leftmost.position[0];
    const currentX = currentNode.position[0];
    return currentX < leftmostX ? currentNode : leftmost;
  });

  // Get the sticky note's width dynamically from its own parameters
  const stickyNoteWidth = stickyNote.parameters.width;

  // Calculate the new position
  const newX = leftmostNode.position[0] - stickyNoteWidth - X_OFFSET;
  const newY = leftmostNode.position[1];

  // Update the 'position' property on the sticky note object
  stickyNote.position = [newX, newY];
}

// 4. Add the updated sticky note to the workflow's 'nodes' array
// Ensure the 'nodes' array exists before trying to push to it.
if (!finalWorkflow.nodes) {
  finalWorkflow.nodes = [];
}
finalWorkflow.nodes.push(stickyNote);

// 5. Return the final, modified workflow in a standard n8n structure
return {
  ...inpdata.json,
  origWorkflow: finalWorkflow
};

