// Prepare Wikipedia lookup terms from research
const tavilyResults = $('Tavily Research').first().json.results || [];
const articleTitle = $('Airtable Status Change').first().json.fields.Title;
const keywords = $('Airtable Status Change').first().json.fields['Primary Keywords'] || '';

// Extract key terms for Wikipedia lookup
const keyTerms = new Set();

// Add main keywords
keywords.split(',').forEach(kw => {
  const term = kw.trim();
  if (term.length > 2) keyTerms.add(term);
});

// Extract important terms from Tavily results
tavilyResults.slice(0, 5).forEach(result => {
  const words = result.title.split(/\s+/);
  words.forEach(word => {
    if (word.length > 4 && /^[A-Z]/.test(word)) {
      keyTerms.add(word);
    }
  });
});

// Limit to 5 terms max
const termsArray = Array.from(keyTerms).slice(0, 5);

return termsArray.map(term => ({
  json: {
    term: term,
    articleId: $('Airtable Status Change').first().json.id,
    articleTitle: articleTitle
  }
}));

// Aggregate all research data
const allWikiItems = $input.all();
const tavilyData = $('Tavily Research').first().json;
const perplexityData = $('Perplexity Deep Research').first().json;
const articleData = $('Airtable Status Change').first().json;

// Collect successful Wikipedia results
const wikiData = allWikiItems
  .filter(item => item.json.extract && !item.json.type?.includes('not_found'))
  .map(item => ({
    title: item.json.title || '',
    summary: item.json.extract || '',
    url: item.json.content_urls?.desktop?.page || ''
  }));

// Clean Perplexity content with citations
let perplexityContent = '';
if (perplexityData.choices && perplexityData.choices[0]) {
  perplexityContent = perplexityData.choices[0].message.content;
  const citations = perplexityData.citations || [];
  for (let i = 0; i < 10; i++) {
    perplexityContent = perplexityContent.replaceAll(
      `[${i + 1}]`,
      citations[i] ? ` (source: ${citations[i]})` : ''
    );
  }
}

// Compile research data
const researchData = {
  tavily: {
    query: tavilyData.query || '',
    results: (tavilyData.results || []).slice(0, 8).map(r => ({
      title: r.title,
      content: r.content,
      url: r.url,
      rawContent: r.raw_content?.substring(0, 2000) || ''
    }))
  },
  perplexity: perplexityContent,
  wikipedia: wikiData,
  articleId: articleData.id,
  articleTitle: articleData.fields.Title,
  primaryKeywords: articleData.fields['Primary Keywords'] || '',
  targetLength: articleData.fields['Target Length'] || 2000
};

return { json: researchData };

// Combine research synthesis with raw research data
// Změna: Opraven název uzlu, smazáno " - old"
const synthesisOutput = $('Research Synthesis Agent').first().json.output;
const aggregatedData = $('Aggregate Research Data').first().json;

const combinedResearch = {
  synthesis: synthesisOutput,
  rawData: {
    tavily: aggregatedData.tavily,
    perplexity: aggregatedData.perplexity,
    wikipedia: aggregatedData.wikipedia
  },
  articleId: aggregatedData.articleId,
  articleTitle: aggregatedData.articleTitle,
  targetLength: aggregatedData.targetLength
};

return { json: combinedResearch };

// Parse research data and prepare for content generation
const article = $input.first().json;

// Oprava: Odstraněno '.fields', protože vstupní data jsou plochá
// Data v 'Research Data' jsou textový řetězec, takže JSON.parse je správně
const researchData = JSON.parse(article['Research Data'] || '{}');

// Oprava: Pokud je 'Categories' text (ve vstupu je "Nastroje,Automatizace"), 
// je lepší ho rozdělit na pole, pokud to další uzly očekávají.
const categories = typeof article.Categories === 'string' 
  ? article.Categories.split(',') 
  : (article.Categories || []);

return {
  json: {
    articleId: article.id,
    // Oprava: Přímý přístup k vlastnostem (bez .fields)
    title: article.Title, 
    primaryKeywords: article['Primary Keywords'] || '',
    secondaryKeywords: article['Secondary Keywords'] || '',
    categories: categories,
    tags: article.Tags || [], // Ve vstupu už je to pole
    targetLength: article['Target Length'] || 2000,
    research: researchData
  }
};

