// Word count check
// Oprava: Hledáme content buď v .output.content nebo přímo v .content
const contentData = $json.output || $json; 
const content = contentData.content || '';

const trimmed = content.replace(/<[^>]*>/g, ' ').trim();
const wordCount = trimmed.split(/\s+/).filter(Boolean).length;
const charCount = content.length;
const limit = parseInt($('Parse Article Data').first().json.targetLength) || 2000;

return {
  json: {
    ...contentData, // Zachováme původní data (title, slug atd.)
    wordCount,
    charCount,
    wordLimitOver: wordCount > limit * 1.2, // 20% tolerance
    limit,
    // Ujistěte se, že tyto odkazy na předchozí uzly jsou platné
    articleId: $('Parse Article Data').first().json.articleId,
    categories: $('Keyword Strategy Agent').first().json.output?.categories || [],
    tags: $('Keyword Strategy Agent').first().json.output?.tags || []
  }
};

// Merge compressed content back
const originalData = $('Word Count Check').first().json;
const compressedContent = $json.output;

return {
  json: {
    ...originalData,
    content: compressedContent,
    wasCompressed: true
  }
};

// Prepare data for verification
const article = $('Fetch Article for Humanizing').first().json;
const humanizedContent = $json.output;
const researchData = JSON.parse(article.fields['Research Data'] || '{}');

return {
  json: {
    articleId: article.id,
    title: article.fields.Title,
    humanizedContent: humanizedContent,
    originalContent: article.fields.Content,
    research: researchData,
    excerpt: article.fields.Excerpt,
    slug: article.fields.Slug,
    imagePrompt: article.fields['Image Prompt'],
    categories: article.fields.Categories,
    tags: article.fields.Tags
  }
};

// Prepare final content data
const verificationOutput = $json.output;
const prepData = $('Prepare for Verification').first().json;

// Use corrected content if there were issues, otherwise use humanized
const finalContent = verificationOutput.correctedContent || prepData.humanizedContent;

return {
  json: {
    articleId: prepData.articleId,
    title: prepData.title,
    content: finalContent,
    excerpt: prepData.excerpt,
    slug: prepData.slug,
    imagePrompt: prepData.imagePrompt,
    categories: prepData.categories,
    tags: prepData.tags,
    verified: verificationOutput.verified,
    verificationIssues: verificationOutput.issues,
    verificationNotes: verificationOutput.verificationNotes
  }
};

