// Extract base64 image from Imagen response
const response = $input.first().json;
const prepData = $('Prepare Final Content').first().json;

let imageBase64 = '';
let hasImage = false;

if (response.predictions && response.predictions[0]) {
  imageBase64 = response.predictions[0].bytesBase64Encoded;
  hasImage = true;
}

return {
  json: {
    ...prepData,
    imageBase64: imageBase64,
    hasImage: hasImage,
    filename: `${prepData.slug}-featured.png`
  }
};

// Prepare data after image upload
const uploadResponse = $input.first().json;
const prepData = $('Process Image Response').first().json;

return {
  json: {
    ...prepData,
    wpMediaId: uploadResponse.id,
    wpMediaUrl: uploadResponse.source_url || uploadResponse.guid?.rendered || ''
  }
};

// No image available - pass through data
const prepData = $('Process Image Response').first().json;

return {
  json: {
    ...prepData,
    wpMediaId: null,
    wpMediaUrl: ''
  }
};

// Map AI categories and tags to WordPress IDs
const allInputs = $input.all();

// Get categories and tags from inputs
let wpCategories = [];
let wpTags = [];

for (const item of allInputs) {
  if (Array.isArray(item.json)) {
    // Check if it's categories or tags based on content
    if (item.json[0]?.taxonomy === 'category' || item.json[0]?.parent !== undefined) {
      wpCategories = item.json;
    } else {
      wpTags = item.json;
    }
  }
}

// If still empty, try to get from specific nodes
if (wpCategories.length === 0) {
  try {
    const catData = $('Get WP Categories').all();
    wpCategories = catData.flatMap(d => Array.isArray(d.json) ? d.json : [d.json]);
  } catch (e) {}
}

if (wpTags.length === 0) {
  try {
    const tagData = $('Get WP Tags').all();
    wpTags = tagData.flatMap(d => Array.isArray(d.json) ? d.json : [d.json]);
  } catch (e) {}
}

// Get article data
const articleData = $('Fetch Article for Publishing').first().json;
const aiCategories = articleData.fields.Categories || [];
const aiTagsString = articleData.fields.Tags || '';
const aiTags = aiTagsString.split(',').map(t => t.trim()).filter(t => t);
const postId = $('Create WordPress Post').first().json.id;

// Map category names to IDs
const categoryIds = [];
for (const aiCat of aiCategories) {
  const match = wpCategories.find(wpCat =>
    wpCat.name?.toLowerCase().includes(aiCat.toLowerCase()) ||
    aiCat.toLowerCase().includes(wpCat.name?.toLowerCase())
  );
  if (match) {
    categoryIds.push(match.id);
  }
}

// If no category found, use default
if (categoryIds.length === 0) {
  categoryIds.push(1); // Uncategorized
}

// Map tag names to IDs
const tagIds = [];
for (const aiTag of aiTags) {
  const match = wpTags.find(wpTag =>
    wpTag.name?.toLowerCase() === aiTag.toLowerCase() ||
    wpTag.slug?.toLowerCase() === aiTag.toLowerCase().replace(/\s+/g, '-')
  );
  if (match) {
    tagIds.push(match.id);
  }
}

return {
  json: {
    postId: postId,
    categoryIds: categoryIds,
    tagIds: tagIds,
    articleId: articleData.id,
    featuredImageUrl: articleData.fields['Featured Image URL'] || ''
  }
};

// Prepare final WordPress data
const articleData = $('Fetch Article for Publishing').first().json;
const postData = $('Create WordPress Post').first().json;

return {
  json: {
    articleId: articleData.id,
    title: articleData.fields.Title,
    wpPostId: postData.id,
    wpUrl: postData.link || `https://processgate.ai/?p=${postData.id}`
  }
};

