/* ===== n8n Chat — Glassy Tech Theme (Merged) ===== */

:root {
  /* Core palette */
  --chat--color-primary: #e74266;
  --chat--color-primary-shade-50: #db4061;
  --chat--color-primary-shade-100: #cf3c5c;
  --chat--color-secondary: #20b69e;
  --chat--color-secondary-shade-50: #1ca08a;

  --chat--color-white: #ffffff;
  --chat--color-light: rgba(255,255,255,0.10);
  --chat--color-light-shade-50: rgba(255,255,255,0.14);
  --chat--color-light-shade-100: rgba(255,255,255,0.22);
  --chat--color-medium: rgba(255,255,255,0.18);
  --chat--color-dark: #0c1026;
  --chat--color-disabled: #8a8d98;
  --chat--color-typing: #d1d5e0;

  /* Layout */
  --chat--spacing: 1rem;
  --chat--border-radius: 14px;
  --chat--transition-duration: 0.18s;

  /* Window - Centered with max dimensions */
  --chat--window--width: 100%;
  --chat--window--height: 100%;
  --chat--window--max-width: 800px;
  --chat--window--max-height: 800px;
  --chat--window--z-index: 9999;
  --chat--window--border: 1px solid rgba(231,66,102,0.35);
  --chat--window--border-radius: 24px;
  --chat--window--margin-bottom: 0;

  /* Header */
  --chat--header-height: auto;
  --chat--header--padding: calc(var(--chat--spacing) * 0.85);
  --chat--header--background: rgba(12,16,38,0.7);
  --chat--header--color: #e8ecf7;
  --chat--header--border-top: 1px solid rgba(255,255,255,0.06);
  --chat--header--border-bottom: 1px solid rgba(255,255,255,0.06);
  --chat--header--border-left: none;
  --chat--header--border-right: none;
  --chat--heading--font-size: 1.25rem;
  --chat--subtitle--font-size: 0.9rem;
  --chat--subtitle--line-height: 1.6;

  /* Messages */
  --chat--message--font-size: 0.98rem;
  --chat--message--padding: 0.5rem 0.75rem 0.35rem;
  --chat--message--border-radius: 18px;
  --chat--message-line-height: 1.5;
  --chat--message--margin-bottom: 0;

  /* Glass bubbles */
  --chat--message--bot--background: rgba(255,255,255,0.75);
  --chat--message--bot--color: #0f1f1c;
  --chat--message--bot--border: 1px solid rgba(255,255,255,0.10);

  --chat--message--user--background: rgba(231,66,102,0.95);
  --chat--message--user--color: #ffffff;
  --chat--message--user--border: 1px solid rgba(231,66,102,0.35);

  --chat--message--pre--background: rgba(12,16,38,0.55);
  --chat--messages-list--padding: 1rem;

  /* Toggle */
  --chat--toggle--size: 64px;
  --chat--toggle--border-radius: 18px;
  --chat--toggle--background: linear-gradient(135deg, #e74266 0%, #ff6a8a 100%);
  --chat--toggle--hover--background: linear-gradient(135deg, #db4061 0%, #fb5f80 100%);
  --chat--toggle--active--background: linear-gradient(135deg, #cf3c5c 0%, #f15577 100%);
  --chat--toggle--color: #ffffff;

  /* Input */
  --chat--textarea--height: 52px;
  --chat--textarea--max-height: 28rem;
  --chat--input--font-size: 0.98rem;
  --chat--input--border: 1px solid rgba(255,255,255,0.10);
  --chat--input--border-radius: 14px;
  --chat--input--padding: 0.75rem 0.9rem;
  --chat--input--background: rgba(255,255,255,0.06);
  --chat--input--text-color: #e9edf7;
  --chat--input--line-height: 1.5;
  --chat--input--placeholder--font-size: 0.98rem;
  --chat--input--border-active: 1px solid rgba(231,66,102,0.55);
  --chat--input--left--panel--width: 2rem;

  /* Buttons */
  --chat--button--color: #f5f7fb;
  --chat--button--background: linear-gradient(135deg, #e74266, #fb5f80);
  --chat--button--padding: 0.55rem 0.9rem;
  --chat--button--border-radius: 12px;
  --chat--button--hover--color: #ffffff;
  --chat--button--hover--background: linear-gradient(135deg, #db4061, #ff6a8a);
  --chat--close--button--color-hover: #e74266;

  /* Send / File buttons */
  --chat--input--send--button--background: rgba(255,255,255,0.06);
  --chat--input--send--button--color: #e9edf7;
  --chat--input--send--button--background-hover: rgba(231,66,102,0.20);
  --chat--input--send--button--color-hover: #ffffff;

  --chat--input--file--button--background: rgba(255,255,255,0.06);
  --chat--input--file--button--color: #9ee7d9;
  --chat--input--file--button--background-hover: rgba(32,182,158,0.18);
  --chat--input--file--button--color-hover: #ffffff;

  /* Body / Footer */
  --chat--body--background: rgba(12,16,38,0.45);
  --chat--footer--background: rgba(12,16,38,0.6);
  --chat--footer--color: #e9edf7;
}

/* ===== Window Container - Centered with Max Dimensions ===== */
.n8n-chat,
.n8n-chat__window,
.n8n-chat-window,
[class*="n8n-chat__window"] {
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  height: 100%;
  max-width: 800px;
  max-height: 800px;
  margin: 0 auto;
  font-family: 'Inter', 'Poppins', sans-serif;
  background:
    radial-gradient(1000px 500px at 100% -10%, rgba(231,66,102,0.18), transparent 60%),
    radial-gradient(1000px 500px at -10% 110%, rgba(32,182,158,0.15), transparent 55%),
    rgba(12,16,38,0.55);
  border-radius: 24px;
  border: 1px solid rgba(231,66,102,0.35);
  overflow: hidden;
  box-shadow: 
    0 12px 40px rgba(0,0,0,0.45), 
    inset 0 1px 0 rgba(255,255,255,0.03);
  z-index: 9999;
}

@supports (backdrop-filter: blur(8px)) {
  .n8n-chat,
  .n8n-chat__window,
  .n8n-chat-window,
  [class*="n8n-chat__window"] {
    backdrop-filter: blur(14px) saturate(140%);
    -webkit-backdrop-filter: blur(14px) saturate(140%);
  }
}

/* ===== Messages Layout - Column Flow ===== */
.n8n-chat__messages,
.chat-messages-list {
  display: flex;
  flex-direction: column;
  gap: 0.35rem;
}

/* ===== Message Bubbles - Auto-Width with Max ===== */
.chat-message {
  display: block;
  width: fit-content;
  max-width: min(70%, 520px);
  margin-bottom: 0 !important;
  border-radius: 18px;
  padding: 0.5rem 0.75rem 0.35rem;
  white-space: normal;
  word-break: break-word;
  box-shadow: 0 8px 24px rgba(0,0,0,0.25);
}

/* Safari / old browsers fallback */
@supports not (width: fit-content) {
  .chat-message {
    display: inline-block;
  }
}

/* Bot message - left aligned */
.chat-message.bot,
.chat-message--bot,
.chat-message.assistant {
  background: rgba(255,255,255,0.75);
  color: #0f1f1c;
  border-radius: 18px 18px 18px 6px;
  margin-right: auto;
  backdrop-filter: blur(6px);
  -webkit-backdrop-filter: blur(6px);
}

/* User message - right aligned */
.chat-message.user,
.chat-message--user,
.chat-message.mine {
  margin-left: auto;
  background: rgba(231,66,102,0.95);
  color: #ffffff;
  border-radius: 18px 18px 6px 18px;
  box-shadow:
    0 8px 24px rgba(0,0,0,0.35),
    0 0 0 1px rgba(231,66,102,0.22) inset;
}

/* ===== Header ===== */
.n8n-chat__header,
[class*="n8n-chat__header"] {
  box-shadow: inset 0 -1px 0 rgba(255,255,255,0.06);
}

/* ===== Code Blocks ===== */
.n8n-chat pre,
.n8n-chat code,
pre code {
  background: var(--chat--message--pre--background) !important;
  color: #e9edf7 !important;
  border: 1px solid rgba(255,255,255,0.10);
  border-radius: 12px;
}

/* ===== Input - Hide Scrollbar ===== */
.n8n-chat textarea,
[class*="textarea"] {
  overflow-y: auto;
  scrollbar-width: none;
}

.n8n-chat textarea::-webkit-scrollbar {
  display: none;
}

/* ===== Send Button ===== */
[class*="send-button"],
.n8n-chat__send-button {
  border: 1px solid rgba(255,255,255,0.12);
  box-shadow: 0 6px 18px rgba(231,66,102,0.25);
}

/* ===== Toggle Button ===== */
.n8n-chat__toggle,
[class*="n8n-chat__toggle"] {
  box-shadow:
    0 0 0 2px rgba(255,255,255,0.10),
    0 10px 26px rgba(231,66,102,0.45);
  border: 1px solid rgba(255,255,255,0.12);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
}

