// 1. Najdeme textovou odpověď z AI (zkoušíme různé názvy proměnných)
const aiResponse = $input.item.json.text || $input.item.json.output || $input.item.json.response || "";

// 2. Najdeme první složenou závorku '{' a poslední '}'
const firstBracket = aiResponse.indexOf('{');
const lastBracket = aiResponse.lastIndexOf('}');

// 3. Kontrola chyb
if (firstBracket === -1 || lastBracket === -1) {
  throw new Error("AI nevrátila žádný JSON. Odpověď byla: " + aiResponse);
}

// 4. Vyřízneme přesně jen JSON část (ignorujeme text okolo)
const jsonString = aiResponse.substring(firstBracket, lastBracket + 1);

try {
  // 5. Převedeme na objekt a pošleme dál
  const parsedObject = JSON.parse(jsonString);
  return { json: { workflowObject: parsedObject } };
} catch (e) {
  throw new Error("Text mezi závorkami není platný JSON. Chyba: " + e.message);
}
