// Vezme textový výstup z AI a převede ho na skutečný JSON objekt
const aiOutput = $input.item.json.output;
return JSON.parse(aiOutput);

// 1. Získání dat z předchozích uzlů
let categoriesData = [];
let tagsData = [];

try {
  categoriesData = $('Get CZ Categories Details').all().map(item => item.json);
} catch (e) {}

try {
  tagsData = $('Get CZ Tags Details').all().map(item => item.json);
} catch (e) {}

// 2. Funkce pro vytažení EN ID
function getEnIds(items) {
  const enIds = [];
  const flatItems = items.flat(); 
  for (const item of flatItems) {
    if (item && item.translations && item.translations.en) {
      enIds.push(item.translations.en);
    }
  }
  return enIds;
}

const enCategoryIds = getEnIds(categoriesData);
const enTagIds = getEnIds(tagsData);

// 3. Získání ID článků pro propojení
// ID nového EN článku (z předchozího kroku)
const enPostId = $('Set image ID for the post').first().json.id;
// ID původního CZ článku (musíme sáhnout hlouběji do historie workflow)
const czPostId = $('Get Post for Translation').first().json.id;

// 4. Příprava objektu 'translations' pro Polylang
// Toto řekne WP: "CS verze je článek X, EN verze je článek Y"
const translationsMap = {
  'cs': czPostId,
  'en': enPostId
};

// 5. Výstup
return {
  json: {
    requestBody: {
      categories: enCategoryIds,
      tags: enTagIds,
      translations: translationsMap, // <-- TOTO propojí články
      status: 'publish'
    },
    postId: enPostId
  }
};

