const pracovniEvents = $input.item.json.pracovni || [];
const osobniEvents = $input.item.json.osobni || [];
const hobbyEvents = $input.item.json.hobby || [];

function formatTime(dateString) {
  if (!dateString) return 'Cel칳 den';
  const date = new Date(dateString);
  return date.toLocaleTimeString('cs-CZ', { hour: '2-digit', minute: '2-digit' });
}

function formatDate(dateString) {
  const date = new Date(dateString);
  return date.toLocaleDateString('cs-CZ', { weekday: 'long', day: 'numeric', month: 'long', year: 'numeric' });
}

function formatEvents(events, calendarName, emoji) {
  if (events.length === 0) return `<h3>${emoji} ${calendarName}</h3><p style="color: #666;">콯치dn칠 ud치losti</p>`;

  let html = `<h3>${emoji} ${calendarName}</h3><ul style="list-style-type: none; padding: 0;">`;

  events.forEach(event => {
    const time = formatTime(event.start?.dateTime || event.start?.date);
    const title = event.summary || 'Bez n치zvu';
    const location = event.location ? `<br><small style="color: #666;">游늸 ${event.location}</small>` : '';
    const description = event.description ? `<br><small style="color: #888;">${event.description.substring(0, 100)}${event.description.length > 100 ? '...' : ''}</small>` : '';

    html += `<li style="margin-bottom: 15px; padding: 10px; background-color: #f5f5f5; border-radius: 5px;">
      <strong>${time}</strong> - ${title}${location}${description}
    </li>`;
  });

  html += '</ul>';
  return html;
}

