const wordLimit = $('Set Draft Post').first().json.post_word_count
function countWordsAndCharacters(text) {
  // Trim to avoid counting whitespace at start/end
  const trimmed = text.trim();

  // Character count (including spaces & newlines)
  const charCount = trimmed.length;

  // Word count — split by any whitespace & filter empty results
  const wordCount = trimmed.split(/\s+/).filter(Boolean).length;

  const wordLimitOver =  wordCount > wordLimit

  return { wordCount, wordLimitOver, wordLimit, charCount };
}

