// This helper function escapes special regex characters.
function escapeRegExp(string) {
  // $& means the whole matched string
  return string.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}

// Loop over all input items
for (const item of $input.all()) {

  // --- 1. Get Data ---
  let markdownText = item.json.full_markdown_text;

  let linksArray = [];
  if (item.json.output && Array.isArray(item.json.output.selected_links)) {
    linksArray = item.json.output.selected_links;
  } else if (Array.isArray(item.json.selected_links)) {
    linksArray = item.json.selected_links;
  }

  // --- 2. Process Links ---
  if (markdownText && linksArray.length > 0) {

    for (const link of linksArray) {

      // 1. The "clean" anchor text from the AI
      // e.g., "how to choose an automation partner"
      const plainAnchor = link.anchor_text;

      // 2. The new markdown link to insert
      // e.g., "[how to choose an automation partner](https://...)"
      const markdownLink = `[${plainAnchor}](${link.destination_url})`;

      // 3. Escape the anchor text so it can be safely used in a Regex
      const escapedAnchor = escapeRegExp(plainAnchor);

      // 4. Create a new Regular Expression
      // This looks for the text in multiple formats:
      // - ``text`` (double backticks)
      // - `text` (single backticks)
      // - **text** (bold)
      // - *text* (italic) - Note: needs \ to escape the *
      // - text (plain)
      // The "g" flag ensures it replaces all instances, though we 
      // typically only want the first. .replace() without 'g' only does first.
      // We will remove 'g' to only replace the first instance.
      const searchRegex = new RegExp(
        `(\`\`${escapedAnchor}\`\`|` +  // ``text``
        `\`${escapedAnchor}\`|` +    // `text`
        `\\*\\*${escapedAnchor}\\*\\*|` + // **text**
        `\\*${escapedAnchor}\\*|` +  // *text*
        `${escapedAnchor})`         // text
      );

      // 5. Replace the *first* found occurrence
      markdownText = markdownText.replace(searchRegex, markdownLink);
    }
  }

  // --- 3. Set Output ---
  item.json.updated_markdown = markdownText;
}

// Return all the modified items
return $input.all();

