  '<img src="$1" alt="$2" width="450" height="auto" style="max-width:450px;height:auto;display:block;margin:15px 0;" />');

const metadata = JSON.stringify({
  name: docName,
  mimeType: "application/vnd.google-apps.document",
  parents: [folderId]
});

const htmlWithStyles = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="UTF-8">
  <style>
    p, ul, ol, table, h1, h2, h3, h4, h5, h6 {
      margin-bottom: 10pt;
    }
    h2 {
      margin-top: 20pt;
    }
    li {
       margin-bottom: 2pt;
    }
  </style>
</head>
<body>
  ${htmlContent}
</body>
</html>
`;

let body = `--${boundary}\r\n`;
body += `Content-Type: application/json; charset=UTF-8\r\n`;
body += `\r\n`;
body += `${metadata}\r\n`;
body += `--${boundary}\r\n`;
body += `Content-Type: text/html\r\n`;
body += `\r\n`;
body += `${htmlWithStyles}\r\n`;
body += `--${boundary}--\r\n`;

return {
  rawData: body 
};

// Loop over all input items
for (const item of $input.all()) {

  // Get the original data from the 'output' object
  const article = item.json.output.completed_article;
  const originalImages = item.json.output.images;

  // Create the new, "extracted" array
  const extractedImages = originalImages.map(image => {
    return {
      url: image.url,
      filename: image.filename
    };
  });

  // Overwrite the item's JSON with the new, flat structure
  // This keeps the article and adds the new extracted_images array
