function findAllDifferences(oldText, newText) {
  // Normalize line endings and split into lines
  const oldLines = (oldText || '').replace(/\r\n/g, '\n').split('\n');
  const newLines = (newText || '').replace(/\r\n/g, '\n').split('\n');

  const maxLines = Math.max(oldLines.length, newLines.length);
  const changes = [];

  for (let i = 0; i < maxLines; i++) {
    const oldLine = oldLines[i];
    const newLine = newLines[i];

    // Handle undefined lines (one text is longer than the other)
    if (oldLine === undefined) {
      if (newLine.trim().length > 0) { // Only log non-empty additions
        changes.push(`Added line ${i + 1}: "${newLine.trim()}"`);
      }
    } else if (newLine === undefined) {
      if (oldLine.trim().length > 0) { // Only log non-empty deletions
        changes.push(`Removed line ${i + 1}: "${oldLine.trim()}"`);
      }
    } else {
      // Normalize both lines for comparison (remove formatting differences)
      const normalizedOld = oldLine.trim()
        .replace(/\\/g, '') // Remove escape characters
        .replace(/^[*-]\s+/g, ' ') // Normalize bullet points
        .replace(/^#+\s*\d+\\\.\s*/g, (match) => match.replace('\\', '')); // Fix escaped numbers in headers

      const normalizedNew = newLine.trim()
        .replace(/\\/g, '')
        .replace(/^[*-]\s+/g, ' ')
        .replace(/^#+\s*\d+\\\.\s*/g, (match) => match.replace('\\', ''));

      // Only log if there's an actual content difference after normalization
      if (normalizedOld !== normalizedNew) {
        changes.push(`Line ${i + 1} was modified.
  Old: "${oldLine.trim()}"
  New: "${newLine.trim()}"`);
      }
    }
  }

  return changes.length > 0 ? changes.join('\n\n') : null;
}

