// Schema Markup Generator for Article E-E-A-T
// Generates structured data for Google to understand article authority

// Extract article data

const articleTitle = $('UpdateStatus1').first().json.fields["Article Title"] || 'Untitled Article';

// const mainImageUrl = article.output?.images?.[0]?.url || '';

const articleContent = $('E-E-A-T Enhancement Agent').first().json.output.enhanced_article || '';

const companyName = $('SetJSON').first().json["Company name"] || '';

const websiteUrl = $('SetJSON').first().json["Website URL"] || '';

const currentDate = $('SetInputs1').first().json["Current Date"] || new Date().toISOString();

const keyword = $('SetInputs1').first().json.Keyword || '';

// Calculate reading time (word count / 200 words per minute)
const wordCount = articleContent.split(/s+/).length;
const readingTimeMinutes = Math.ceil(wordCount / 200);

// Format ISO date
const isoDate = new Date(currentDate).toISOString();

// Generate Article Schema
const articleSchema = {
 "@context": "https://schema.org",
 "@type": "Article",
 "headline": articleTitle,
 "description": `Comprehensive guide about ${keyword}`,
 "image": `${websiteUrl}/default-image.jpg`,// mainImageUrl || `${websiteUrl}/default-image.jpg`,
 "datePublished": isoDate,
 "dateModified": isoDate,
 "author": {
 "@type": "Person",
 "name": `${companyName} Editorial Team`,
 "url": websiteUrl
 },
 "publisher": {
 "@type": "Organization",
 "name": companyName,
 "url": websiteUrl,
 "logo": {
 "@type": "ImageObject",
 "url": `${websiteUrl}/logo.png`
 }
 },
 "mainEntityOfPage": {
 "@type": "WebPage",
 "@id": `${websiteUrl}/blog/${keyword.toLowerCase().replace(/s+/g, '-')}`
 },
 "wordCount": wordCount,
 "timeRequired": `PT${readingTimeMinutes}M`,
 "articleBody": articleContent.substring(0, 500) + '...',
 "keywords": keyword
};

// Generate BreadcrumbList Schema
const breadcrumbSchema = {
 "@context": "https://schema.org",
 "@type": "BreadcrumbList",
 "itemListElement": [
 {
 "@type": "ListItem",
 "position": 1,
 "name": "Home",
 "item": websiteUrl
 },
 {
 "@type": "ListItem",
 "position": 2,
 "name": "Blog",
 "item": `${websiteUrl}/blog`
 },
 {
 "@type": "ListItem",
 "position": 3,
 "name": articleTitle,
 "item": `${websiteUrl}/blog/${keyword.toLowerCase().replace(/s+/g, '-')}`
 }
 ]
};

// Generate Organization Schema for E-E-A-T
const organizationSchema = {
 "@context": "https://schema.org",
 "@type": "Organization",
 "name": companyName,
 "url": websiteUrl,
 "logo": `${websiteUrl}/logo.png`,
 "sameAs": [
 // Add social media URLs if available
 ],
 "contactPoint": {
 "@type": "ContactPoint",
 "contactType": "Customer Service",
 "url": websiteUrl
 }
};

// Combine all schemas
const combinedSchema = {
 "@context": "https://schema.org",
 "@graph": [
 articleSchema,
 breadcrumbSchema,
 organizationSchema
 ]
};

// Format for HTML insertion
const schemaMarkupHtml = `<script type="application/ld+json">
${JSON.stringify(combinedSchema, null, 2)}
</script>`;

// Return all data
return {
 json: {
 schema_markup_json: combinedSchema,
 schema_markup_html: schemaMarkupHtml,
 reading_time_minutes: readingTimeMinutes,
 word_count: wordCount,
 article_url: `${websiteUrl}/blog/${keyword.toLowerCase().replace(/s+/g, '-')}`,
 publish_date: isoDate
 }
};

