// 1. Get the raw text
let raw = items[0].json.data || items[0].json.text || JSON.stringify(items[0].json);

// 2. Clean weird trailing commas or semicolons that break JSON
raw = raw.trim();

// Fix cases where there is a trailing semicolon (common in Postiz output)
raw = raw.replace(/;$/, "");

// 3. Wrap missing brackets if needed
// If the text does NOT start with "[" but contains multiple "{ ... }, { ... }"
if (!raw.trim().startsWith("[")) {
    raw = "[" + raw + "]";
}

// 4. Try parsing
let arr;
try {
    arr = JSON.parse(raw);
} catch(e) {
    throw new Error("Could not parse JSON. Cleaned string:\n" + raw);
}

// 5. Return each object as its own item
return arr.map(obj => ({ json: obj }));

