// Flatten a media array from Airtable into individual items
// Each item will carry the original record info and its index in the array

return items.flatMap((item, recordIndex) => {
  const mediaArray = item.json.Carousel || []; // replace 'Media' with your Airtable field name

  return mediaArray.map((media, mediaIndex) => ({
    json: {
      originalRecordIndex: recordIndex,  // index of the Airtable record
      mediaIndex: mediaIndex,            // index of the file in the array
      filename: media.filename,          // filename from Airtable
      url: media.url                     // URL from Airtable
    }
  }));
});

// Sort the items by mediaIndex and return as array
return [
  {
    json: {
      bodyArray: items
        .sort((a, b) => a.json.mediaIndex - b.json.mediaIndex)
        .map(item => ({
          id: item.json.id,
          path: item.json.path
        }))
    }
  }
];

