const allowed = [];
const filteredOut = [];

// Iterate through each item
items.forEach(item => {
  // Access binary data; replace 'data' if your binary property is different
  const binaryProperty = item.binary?.data;

  // Get MIME type either from binary or headers if available
  const mimeType = binaryProperty?.mimeType || item.json?.headers?.['content-type'] || '';

  // Access the original URL from your field
  const originalUrl = item.json?.Post_urls;

  // Check MIME type
  if (binaryProperty && (mimeType === 'image/jpeg' || mimeType === 'image/png')) {
    // Keep the item, retain the URL
    allowed.push({
      json: { ...item.json, Post_urls: originalUrl },
      binary: item.binary
    });
  } else {
    // Filtered out items for logging/debugging
    filteredOut.push({
      json: { ...item.json, Post_urls: originalUrl },
      binary: item.binary
    });
  }
});

// Log filtered out URLs
console.log('Filtered out URLs:', filteredOut.map(i => i.json.Post_urls));

// Return only allowed items
return allowed;

// 1. Get all incoming items
const allItems = $input.all();

// 2. Filter: Keep ONLY items that actually contain image data
// This ignores the "carousel_slide_prompts" items that caused the crash
const imageItems = allItems.filter(item => item.json.data && item.json.data.url);

// 3. Format the valid images for Airtable
const attachments = imageItems.map(item => {
  return {
    "url": item.json.data.url,
    "filename": item.json.data.url
  };
});

// 4. Get the Post Body from the previous node
// We use a try/catch or logical OR to prevent crashing if the node isn't found
let postBody = "";
try {
    // Attempt to grab post_body from the Loop or Split node
    // Make sure 'Split Out Prompts' matches your node name exactly
    postBody = $('Split Out').first().json.post_body;
} catch (error) {
    postBody = "Post body not found or node name incorrect";
}

// 5. Return combined data
return [
  {
    json: {
      post_body: postBody,
      attachments: attachments
    }
  }
];

