// Initialize an object to hold the groups
const groupedResult = {};

// Loop over input items
for (const item of $input.all()) {

  // Get the value to group by. 
  // We use || 'Uncategorized' to handle missing fields or empty objects
  const groupKey = item.json.Type_Content || 'Uncategorized';

  // If this group key doesn't exist yet in our object, create it with an empty array
  if (!groupedResult[groupKey]) {
    groupedResult[groupKey] = [];
  }

  // Add the current item's JSON data to the correct group array
  groupedResult[groupKey].push(item.json);
}

// Convert the grouped object back into an n8n-friendly array format
const output = [];

for (const key of Object.keys(groupedResult)) {
  output.push({
    json: {
      type: key, // The name of the group (e.g., "News")
      count: groupedResult[key].length, // How many items are in this group
      items: groupedResult[key] // The array of actual items
    }
  });
}

return output;

// Loop over input items
for (const item of $input.all()) {
  const jsonData = item.json;

  // 1. Find the key that starts with "Link-"
  const linkKey = Object.keys(jsonData).find(key => key.startsWith('Link-'));

  if (linkKey) {
    // 2. Extract the suffix (e.g., "ideas")
    const extractedInputName = linkKey.replace('Link-', '').toLowerCase();
    jsonData.extracted_origin = extractedInputName;

    // 3. Extract the Record ID from the value of that field
    // Checks if it is an array and has at least one item
    if (Array.isArray(jsonData[linkKey]) && jsonData[linkKey].length > 0) {
      jsonData.extracted_record_id = jsonData[linkKey][0];
    } else {
      jsonData.extracted_record_id = null;
    }

    // 4. Find the matching table_url from the 'inputs' array
    if (jsonData.inputs && Array.isArray(jsonData.inputs)) {
      const match = jsonData.inputs.find(entry => entry.input.toLowerCase() === extractedInputName);
      jsonData.matched_table_url = match ? match.table_url : null;
    } else {
      jsonData.matched_table_url = null;
    }

  } else {
    // Handle cases where no 'Link-' field exists
    jsonData.extracted_origin = null;
    jsonData.extracted_record_id = null;
    jsonData.matched_table_url = null;
  }
}

return $input.all();

