for (const item of items) {
    // Získání celého bloku 'fields' z Webhooku
    const fields = $('Webhook').first().json.body.data.fields;

    // ==========================================================
    // 1. Datum narození (načítáme z pole 'datum_narozeni')
    // ==========================================================
    // ZDE BYLA ZMĚNA: Používáme správný název pole
    const isoDateString1 = fields.datum_narozeni;

    if (isoDateString1) {
        const dateObj1 = new Date(isoDateString1);
        // Protože datum končí na Z (UTC), getUTCDate vrátí správný den
        const day1 = dateObj1.getUTCDate();
        const month1 = dateObj1.getUTCMonth() + 1; 
        const year1 = dateObj1.getUTCFullYear();

        // Výsledek např: 10.7.2006
        item.json.datum_narozeni_formatovano = `${day1}.${month1}.${year1}`;
    } else {
        item.json.datum_narozeni_formatovano = 'CHYBA: Datum narození nenalezeno';
    }

    // ==========================================================
    // 2. Datum vytvoření smlouvy (AKTUÁLNÍ DATUM)
    // ==========================================================
    const dateObj2 = new Date(); 

    const day2 = dateObj2.getDate();
    const month2 = dateObj2.getMonth() + 1; 
    const year2 = dateObj2.getFullYear();

    item.json.datum_vytvoreni_smlouvy = `${day2}.${month2}.${year2}`;
}

return items;

// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
  item.json.myNewField = 1;
}

return $input.all();
