// Group profiles by platform based on domain and tracked platforms
const allProfiles = $input.all();
const platforms = {
  twitter: [],
  tiktok: [],
  linkedin: []
};

allProfiles.forEach(item => {
  const profile = item.json;
  const url = profile.URL;
  const trackedPlatformIds = profile['Tracked Platforms'] || []; // Array of platform record IDs

  if (!url || trackedPlatformIds.length === 0) return;

  // Determine platform based on domain
  let platform = null;
  if (url.includes('x.com') || url.includes('twitter.com')) {
    platform = 'twitter';
  } else if (url.includes('tiktok.com')) {
    platform = 'tiktok';
  } else if (url.includes('linkedin.com')) {
    platform = 'linkedin';
  }

  if (platform && platforms[platform]) {
    // Extract username from URL
    let username = '';
    let fullUrl = url;

    if (platform === 'twitter') {
      // For Twitter/X: extract username from x.com/username
      const urlParts = url.split('/');
      username = urlParts[urlParts.length - 1] || urlParts[urlParts.length - 2];
      username = username.replace('@', ''); // Remove @ if present
    } else if (platform === 'tiktok') {
      // For TikTok: extract username from tiktok.com/@username
      const tiktokMatch = url.match(/@([^\/\?]+)/);
      if (tiktokMatch) {
        username = tiktokMatch[1];
      } else {
        // Fallback to last part of URL
        const urlParts = url.split('/');
        username = urlParts[urlParts.length - 1] || urlParts[urlParts.length - 2];
      }
    } else if (platform === 'linkedin') {
      // For LinkedIn: extract username from URL like linkedin.com/in/username
      const linkedinMatch = url.match(/linkedin\.com\/in\/([^\/\?]+)/);
      if (linkedinMatch) {
        username = linkedinMatch[1];
      } else {
        // Fallback to last part of URL
        const urlParts = url.split('/');
        username = urlParts[urlParts.length - 1] || urlParts[urlParts.length - 2];
      }

      // Ensure full URL starts with https://
      if (!url.startsWith('http')) {
        fullUrl = 'https://' + url;
      } else {
        fullUrl = url;
      }
    }

    platforms[platform].push({
      profileId: profile.id,
      profileName: profile['Competitor Name'],
      profileUrl: url,
      username: username, // For LinkedIn, this is just the username part
      fullUrl: platform === 'linkedin' ? fullUrl : url, // Store full URL for LinkedIn API
      platform: platform,
      platformRecordId: trackedPlatformIds[0] // Use the first tracked platform ID (assuming one platform per competitor URL)
    });
  }
});

console.log('Grouped profiles:', platforms);

// Create a map of platform names to their record IDs from the profiles
const platformRecordIds = {};
Object.keys(platforms).forEach(platformName => {
  if (platforms[platformName].length > 0) {
    // Get the platform record ID from the first profile of this platform type
    platformRecordIds[platformName] = platforms[platformName][0].platformRecordId;
  }
});

console.log('Platform record IDs:', platformRecordIds);

// Prepare scraper inputs for each platform
const scraperConfigs = [];

// Twitter/X config
if (platforms.twitter.length > 0) {
  const twitterHandles = platforms.twitter.map(p => p.username);

  scraperConfigs.push({
    platform: 'twitter',
    platformRecordId: platformRecordIds['twitter'],
    profiles: platforms.twitter,
    scraperActor: 'apidojo~twitter-scraper-lite',
    inputConfig: {
      maxItems: 200,
      searchTerms: [],
      sort: 'Top',
      twitterHandles: twitterHandles
    }
  });
}

// TikTok config
if (platforms.tiktok.length > 0) {
  const tiktokProfiles = platforms.tiktok.map(p => p.username);

  scraperConfigs.push({
    platform: 'tiktok',
    platformRecordId: platformRecordIds['tiktok'],
    profiles: platforms.tiktok,
    scraperActor: 'clockworks~tiktok-scraper',
    inputConfig: {
      excludePinnedPosts: false,
      profiles: tiktokProfiles,
      proxyCountryCode: 'None',
      resultsPerPage: 2,
      scrapeRelatedVideos: false,
      shouldDownloadAvatars: false,
      shouldDownloadCovers: false,
      shouldDownloadMusicCovers: false,
      shouldDownloadSlideshowImages: false,
      shouldDownloadSubtitles: false,
      shouldDownloadVideos: true
    }
  });
}

// LinkedIn config  
if (platforms.linkedin.length > 0) {
  const linkedinUrls = platforms.linkedin.map(p => p.fullUrl);

  scraperConfigs.push({
    platform: 'linkedin',
    platformRecordId: platformRecordIds['linkedin'],
    profiles: platforms.linkedin,
    scraperActor: 'apimaestro~linkedin-batch-profile-posts-scraper',
    inputConfig: {
      limit: 10,
      usernames: linkedinUrls // These are full URLs for the API
    }
  });
}

console.log('Scraper configs:', scraperConfigs);
return scraperConfigs;

// Universal post normalizer
const platform = $('Store Run Data').first().json.runData.platform;
const platformRecordId = $('Store Run Data').first().json.runData.platformRecordId;
const profiles = $('Store Run Data').first().json.runData.profiles;
const posts = $input.all();

console.log("profiles", profiles);
console.log("platformRecordId", platformRecordId);

// Create username to profile map for easier lookup
const profileMap = {};
profiles.forEach(p => {
  profileMap[p.username.toLowerCase()] = p;
});

// Helper function to format media for Airtable attachment field
function formatMediaForAirtable(url) {
  if (!url) return null;
  return [{
    url: url
  }];
}

// Function to determine media type and get the best URL for Twitter
function getTwitterMediaInfo(post) {
  // Check extendedEntities first (preferred), then fall back to media array
  const mediaArray = post.extendedEntities?.media || [];

  if (mediaArray.length === 0) {
    return { type: null, url: null, videoUrl: null };
  }

  const mediaItem = mediaArray[0]; // Get first media item
  const mediaType = mediaItem.type; // 'video', 'photo', 'animated_gif'

  if (mediaType === 'video') {
    // For videos, use the thumbnail image URL for Airtable display
    const thumbnailUrl = mediaItem.media_url_https;
    // Get the best quality MP4 video URL
    const videoVariants = mediaItem.video_info?.variants || [];
    const mp4Videos = videoVariants.filter(v => v.content_type === 'video/mp4');
    const bestVideo = mp4Videos.sort((a, b) => (b.bitrate || 0) - (a.bitrate || 0))[0];

    return {
      type: 'Video',
      url: thumbnailUrl, // Thumbnail for Airtable preview
      videoUrl: bestVideo?.url || null // Actual video URL
    };
  } else if (mediaType === 'photo') {
    return {
      type: 'Image',
      url: mediaItem.media_url_https,
      videoUrl: null
    };
  } else if (mediaType === 'animated_gif') {
    // For GIFs, we can use the video variant URL for better quality
    const videoVariants = mediaItem.video_info?.variants || [];
    const mp4Videos = videoVariants.filter(v => v.content_type === 'video/mp4');
    const gifVideo = mp4Videos[0]; // Usually just one variant for GIFs

    return {
      type: 'GIF',
      url: gifVideo?.url || mediaItem.media_url_https, // Use video URL if available, fallback to static
      videoUrl: gifVideo?.url || null
    };
  }

  return { type: null, url: null, videoUrl: null };
}

// Normalize posts based on platform
const normalizedPosts = [];

console.log("post", posts);
posts.forEach(item => {
  const post = item.json;
  let normalized = null;

  try {
    switch(platform) {
      case 'twitter':
        if (post.author?.userName) {
          const username = post.author.userName.toLowerCase();
          const profile = profileMap[username];

          if (profile) {
            // Get media info using the new function
            const mediaInfo = getTwitterMediaInfo(post);

            normalized = {
              // Field names must match exactly with your Airtable field names
              "Competitor": [profile.profileId], // Array with competitor record ID
              "Platform": [platformRecordId], // Array with platform record ID
              "PlatformName": "twitter",
              "Content Title": post.fullText?.substring(0, 100) || '', // First 100 chars as title
              "Content": post.fullText || '',
              "Content URL": post.url || '',
              "Media": formatMediaForAirtable(mediaInfo.url), // Format as attachment array
              "Media Type": mediaInfo.type, // 'Video', 'Image', 'GIF', or null
              "Video URL": mediaInfo.videoUrl || null, // Direct video URL if it's a video/GIF
              "Publish Date": post.createdAt || new Date().toISOString(),
              "Views": post.viewCount || 0,
              "Likes": post.likeCount || 0,
              "Comments": post.replyCount || 0,
              "Shares": post.retweetCount || 0,
              "Imported At": new Date().toISOString()
            };
          }
        }
        break;

      case 'tiktok':
        if (post.authorMeta?.name) {
          const username = post.authorMeta.name.toLowerCase();
          const profile = profileMap[username];

          if (profile) {
            const mediaUrl = $input.first().json.webVideoUrl;

            normalized = {
              "Competitor": [profile.profileId],
              "Platform": [platformRecordId],
              "PlatformName": "tiktok",
              "Content Title": post.text?.substring(0, 100) || '',
              "Content": post.text || '',
              "Content URL": post.webVideoUrl || '',
              "Media": formatMediaForAirtable($input.first().json.videoMeta.coverUrl), 
              "Media Type": 'Video', // Capital 'V' to match Airtable options
              "Publish Date": post.createTimeISO || new Date().toISOString(),
              "Views": post.playCount || 0,
              "Likes": post.diggCount || 0,
              "Comments": post.commentCount || 0,
              "Shares": post.shareCount || 0,
              "Imported At": new Date().toISOString(),
              "Needs Script": true // For transcript fetching
            };
          }
        }
        break;

      case 'linkedin':
        if (post.author?.username) {
          const username = post.author.username.toLowerCase();
          const profile = profileMap[username];

          if (profile) {
            // Handle media based on type field
            let mediaUrl = null;
            let mediaType = null;

            if (post.media) {
              // Check the type field to determine media type
              if (post.media.type === 'image') {
                // For images, use the main URL or get from images array
                mediaUrl = post.media.url || (post.media.images && post.media.images[0]?.url);
                mediaType = 'Image';
              } else if (post.media.type === 'video') {
                // For videos, use the video URL (not thumbnail)
                mediaUrl = post.media.url;
                mediaType = 'Video';
              }
            }
            // Check for document (PDF, presentations, etc.)
            else if (post.document) {
              // Use thumbnail for preview
              mediaUrl = post.document.thumbnail;
              mediaType = 'Image'; // Documents show as images (thumbnails) in feed
            }

            normalized = {
              "Competitor": [profile.profileId],
              "Platform": [platformRecordId],
              "PlatformName": "linkedin",
              "Content Title": post.text?.substring(0, 100) || '',
              "Content": post.text || '',
              "Content URL": post.url || '',
              "Media": formatMediaForAirtable(mediaUrl), // Format as attachment array
              "Media Type": mediaType, // Capitalized to match Airtable options
              "Publish Date": post.posted_at?.date || new Date().toISOString(),
              "Views": 0,
              "Likes": post.stats?.like || 0,
              "Comments": post.stats?.comments || 0,
              "Shares": post.stats?.reposts || 0,
              "Imported At": new Date().toISOString()
            };
          }
        }
        break;
    }

    if (normalized) {
      normalizedPosts.push(normalized);
    }
  } catch (error) {
    console.error(`Error processing post for ${platform}:`, error);
  }
});

console.log(`Normalized ${normalizedPosts.length} posts for ${platform}`);
return normalizedPosts;

// Replace "Add Transcript" with a Code node
const items = $input.all();

return items.map(item => {
  const transcript = item.json.transcript || '';

  // Clean WEBVTT format if present
  let cleanTranscript = transcript;
  if (transcript.startsWith('WEBVTT')) {
    // Remove WEBVTT header and timestamps
    cleanTranscript = transcript
      .split('\n')
      .filter(line => !line.startsWith('WEBVTT') && !line.includes('-->') && line.trim() !== '')
      .join(' ')
      .replace(/\s+/g, ' ')
      .trim();
  }

  return {
    json: {
      ...item.json,  // Preserve ALL existing fields
      Transcript: cleanTranscript  // Add cleaned transcript
    }
  };
});

// Add Platform and Competitor Names
const posts = $input.all();
const enrichedPosts = [];

for (const item of posts) {
  const post = item.json;

  // Initialize enriched post with original data
  let enrichedPost = { ...post };

  // Extract platform record ID
  const platformId = Array.isArray(post.Platform) 
    ? post.Platform[0] 
    : post.Platform;

  // Extract competitor record ID
  const competitorId = Array.isArray(post.Competitor) 
    ? post.Competitor[0] 
    : post.Competitor;

  // Set platform name based on ID (you'd need to maintain this mapping)
  // Or fetch it with another Airtable node
  const platformMap = {
    'recuA14LKBGSnQdjs': 'Twitter',
    'reczTx5Jkw3lk19lk': 'LinkedIn',
    'recqbyAG8LYGlTLVe': 'TikTok'
  };

  enrichedPost.PlatformName = platformMap[platformId] || 'Unknown';
  enrichedPost.PlatformRecordId = platformId;

  enrichedPosts.push({ json: enrichedPost });
}

return enrichedPosts;

