// Process and structure the AI analysis with original post data
const post = $("Loop each viral content").item.json;
// Process and structure the AI analysis with original post data
const input = $input.item.json;
let analysis = {};
if (input.message?.content) {
  try {
    analysis = JSON.parse(input.message.content);
  } catch (e) {
    console.log('Failed to parse AI response:', e);
    analysis = {};
  }
}
console.log("analysis", analysis)
// Calculate additional metrics
const views = parseInt(post.Views) || 0;
const likes = parseInt(post.Likes) || 0;
const comments = parseInt(post.Comments) || 0;
const shares = parseInt(post.Shares) || 0;
const engagementRate = parseFloat(post['Engagement Rate']) ||
  ((likes + comments + shares) / views * 100);
// Calculate viral score based on platform
let viralScore = 0;
let isViral = false;
switch(post.PlatformName) {
  case 'Twitter':
    viralScore = Math.min(100, (views / 100000) * 100);
    isViral = views > 100000;
    break;
  case 'LinkedIn':
    viralScore = Math.min(100, (views / 50000) * 100);
    isViral = views > 50000;
    break;
  case 'TikTok':
    viralScore = Math.min(100, (views / 1000000) * 100);
    isViral = views > 1000000;
    break;
  case 'Instagram':
    viralScore = Math.min(100, (views / 200000) * 100);
    isViral = views > 200000;
    break;
  default:
    viralScore = Math.min(100, (views / 100000) * 100);
    isViral = views > 100000;
}
// Create structured output
return {
  // Post identification
  post_id: post['Post ID'] || post.id,
  platform: post.Platform,
  platformName: post.PlatformName,
  account: post.Account,
  url: post['Post URL'] || post.url,
  created_date: post['Created Date'],

  // Content data
  content: post.Content,
  hashtags: post.Hashtags,
  category: post.Category || analysis.pattern_category,

  // Raw metrics
  views: views,
  likes: likes,
  comments: comments,
  shares: shares,
  engagement_rate: engagementRate.toFixed(2),

  // Pattern analysis from AI
  pattern_name: analysis.pattern_name || `${post.Platform} Pattern`,
  description: analysis.description || '',
  hook_type: analysis.hook_type || 'Unknown',
  hook_example: analysis.hook_example || post.Content?.substring(0, 100),
  body_structure: analysis.body_structure || '',
  cta_type: analysis.cta_type || 'None',
  cta_example: analysis.cta_example || '',

  // Psychological elements (now as strings)
  emotions_evoked: Array.isArray(analysis.emotions_evoked)
    ? analysis.emotions_evoked.join(' / ')
    : '',
  psychological_triggers: Array.isArray(analysis.psychological_triggers)
    ? analysis.psychological_triggers.join(' / ')
    : '',
  visual_elements: Array.isArray(analysis.visual_elements)
    ? analysis.visual_elements.join(' / ')
    : '',

  // Insights
  viral_factors: analysis.viral_factors || '',
  optimization_notes: analysis.optimization_notes || '',
  pattern_category: analysis.pattern_category || post.Category || 'General',

  // Platform specific
  platform_insights: analysis.platform_specific_insights || {},

  // Calculated metrics
  viral_score: viralScore.toFixed(0),
  is_viral: isViral,
  virality_confidence: analysis.virality_confidence,
  share_ratio: ((shares / likes) * 100).toFixed(2),
  comment_ratio: ((comments / likes) * 100).toFixed(2),

  // Performance tier
  performance_tier: isViral ? 'Viral' : engagementRate > 10 ? 'High Engagement' : 'Standard',

  // For aggregation
  pattern_key: `${analysis.hook_type || 'unknown'}-${post.Platform}-${analysis.pattern_category || 'general'}`.toLowerCase().replace(/[^a-z0-9-]/g, '')
};

// Aggregate multiple Airtable records for AI comparison
const records = $input.all().map(item => item.json); // Get all input records

// Structure each record into a simplified object for AI
const organizedPatterns = records.map(record => ({
  id: record.id,
  pattern_name: record["Pattern Name"] || 'Unknown',
  description: record.Description || '',
  hook_type: record["Hook Type"] || '',
  hook_example: record["Hook Example"] || '',
  body_structure: record["Body Structure"] || '',
  cta_type: record["CTA Type"] || '',
  cta_example: record["CTA Example"] || '',
  emotions_evoked: record["Emotions Evoked"] ? record["Emotions Evoked"].split(' / ').map(e => e.trim()) : [],
  psychological_triggers: record["Psychological Triggers"] ? record["Psychological Triggers"].split(' / ').map(t => t.trim()) : [],
  visual_elements: record["Visual Elements"] ? record["Visual Elements"].split(' / ').map(v => v.trim()) : [],
  viral_factors: record["Viral Factors"] || '',
  pattern_category: record["Pattern Category"]?.value || record["Pattern Category"] || 'General',
  platform: record["Platform Name (Lookup)"]?.[0] || 'Unknown'
}));

console.log("patterns", organizedPatterns)
const newPattern = $("Structure Patterns").first().json;
console.log("new pattern", newPattern);
return [{
  json: {
    new_pattern: newPattern,
    patterns: organizedPatterns,
    prompt_summary: `New Pattern:\n${JSON.stringify(newPattern, null, 2)}\n\nExisting Patterns:\n${JSON.stringify(organizedPatterns, null, 2)}`
 }
}]

