// Process the AI response
const aiResponse = $input.first().json.message.content; // Get the content string from the AI response

// Parse the JSON (assuming it's already valid JSON without line breaks)
let responseData;
try {
  responseData = JSON.parse(aiResponse.replace(/\n/g, '')); // Remove line breaks and parse
} catch (e) {
  console.log('Failed to parse AI response:', e);
  return [{ should_update: false }]; // Return array with false if parsing fails
}

// Check if action is "update" (return true), else false
const shouldCreate = responseData.action === "create";
const shouldUpdate = responseData.action === "update";

// Return an array with the boolean and optionally the matching_id
return [{
  should_create: shouldCreate,
  should_update: shouldUpdate,
  matching_id: responseData.action === "update" ? responseData.matching_id : null
}];
