parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "questions": {
      "type": "array",
      "description": "Follow up questions to clarify the prompt goals, max of 3.",
      "items": {
          "type": "string"
      }
    }
  }
}

assignments.value:
=Initial query: {{ $('SetQueryForm').item.json.query }}
Follow-up Questions and Answers:
{{
$input.all()
  .map(item => {
    const q = Object.keys(item.json)[0];
    const a = item.json[q];
    return `question: ${q}\nanswer: ${a}`;
  })
  .join('\n')
}}
